(function(landing) {
    'use strict';

    var errors = {};

    landing.start = function() {

        $('.input-wrap.email .message').on('click', function() {
            $(this).fadeOut();
        });

        $('#subscribed a').on('click', function() {
            $('input[name=email]').on('focus', function() {
                if (typeof ga !== 'undefined') {
                    ga('send', 'event', 'landing', 'share');
                }
            });
        });

        $('input[name=email]').on('focus', function() {
            if (typeof ga !== 'undefined') {
                ga('send', 'event', 'landing', 'focus_input');
            }
        });

        $('input[name=email]').on('blur', function(e) {
            hunk.subscription.validate(true);
        });

        $('#subscribe').on('submit', function(e) {
            e.preventDefault();
            if (hunk.subscription.validate()) {
                hunk.subscription.subscribe();
            }
        });


        if (window.location.hash === '#subscribe') {
            $('input[name=email]').focus();
        }

    };

    landing.hideError = function(selector) {
        if (!!errors[selector]) {
            errors[selector] = false;

            $(selector).removeClass('invalid');
            $(selector + ' .message')
                .fadeOut();
        }
    };

    landing.showError = function(selector) {
        var visible = errors[selector] || false;
        if (!visible) {
            errors[selector] = true;

            $(selector).addClass('invalid');
            $(selector + ' .message').fadeIn();

            setTimeout(function() {
                landing.hideError(selector);
            }, 5000);
        } else {
            $(selector + ' .message').fadeOut(200, function() {
                $(selector + ' .message').fadeIn(200);
            });
        }
    }



    $(function() {
        hunk.start();
    });
}(hunk('landing')));






(function(subscription) {
    'use strict';

    subscription.validate = function(hide) {
        var val = $('input[name=email]').val(),
            at_idx = val.indexOf('@'),
            dot_idx = val.lastIndexOf('.');

        if (val === '' && hide === true) {
            hunk.landing.hideError('.input-wrap.email');
            return false;
        }

        if (at_idx > 0 && dot_idx > 3 &&
            at_idx < dot_idx &&
            val.length > dot_idx + 2) {

            hunk.landing.hideError('.input-wrap.email');
            return true;
        } else {
            hunk.landing.showError('.input-wrap.email');
        }

        return false;
    };

    subscription.subscribe = function() {

        if (!subscription.validate()) {
            return;
        }

        $.post('/ajax/landing_subscribe', $('#subscribe').serialize(),

            function(res) {
                var status = !!res ? res.status : 'error',
                    error = !!res ? res.error : null;

                if (status === 'success') {
                    if (typeof ga !== 'undefined') {
                        ga('send', 'event', 'landing', 'subscribe');
                    }

                    $('#subscribe').hide();
                    $('#subscribed').fadeIn(200);

                } else if (status === 'exists') {

                    $('form .result').html('It seems that you\'re ' +
                        'already registered. Thanks for that :) Do ' +
                        'you want to  <a href="/subscription?id=' + res.id +
                        '">edit your subscription</a>?').show();
                    $('input[name=email]').val('');

                } else if (status === 'error') {
                    $('form .result')
                        .html('Oups, an error occured. You may try again?')
                        .show();
                }

            });
    };

}(hunk('subscription')));
